mapper.select:
SELECT o.*, mi.name "product_name", c.company_name, c.company_tel, s.quantity "stock_quantity" FROM orders o JOIN company c ON o.company_no = c.company_no JOIN masterdata_information mi ON o.product_no = mi.product_no LEFT JOIN (SELECT product_code, SUM(quantity) AS quantity FROM stock GROUP BY product_code) s ON mi.code = s.product_code WHERE delete_status = 0
AND o.order_status = '신청완료' AND o.order_status = '생산중' AND o.order_status = '생산완료' AND o.order_status = '배송중' AND o.order_status = '배송완료' AND o.order_status = '처리완료' AND o.order_status = '취소'
AND ( mi.name LIKE CONCAT('%', #{query}, '%') OR c.company_name LIKE CONCAT('%', #{query}, '%') OR o.order_status LIKE CONCAT('%', #{query}, '%') ) ORDER BY o.order_date DESC LIMIT #{cri.startPage}, #{cri.pageSize}

		SELECT *, name "product_name"
  		FROM masterdata_information
  		WHERE product_no = #{product_no}
