mapper.select:
SELECT identify_code, warehouse_no, io_classification, category, product_name 'name', io_quantities, product_unit 'unit', price, expiry_date, io_date, status FROM stock_info WHERE identify_code IS NOT NULL
AND status = '대기중' AND status = '승인' AND status = '취소' AND io_classification = '입고' AND io_classification = '출고' AND category = '자재' AND category = '완제품'
AND( identify_code LIKE CONCAT('%', #{searchword}, '%') OR io_classification LIKE CONCAT('%', #{searchword}, '%') OR category LIKE CONCAT('%', #{searchword}, '%') OR product_name LIKE CONCAT('%', #{searchword}, '%') OR status LIKE CONCAT('%', #{searchword}, '%') ) ORDER BY io_date DESC LIMIT #{cri.startPage}, #{cri.pageSize}

SELECT *
FROM stock
LIMIT #{startPage},#{pageSize}

SELECT * FROM stock WHERE stock_id IS NOT NULL
AND category = '자재' AND category = '완제품'
AND( product_code LIKE CONCAT('%', #{searchword}, '%') OR category LIKE CONCAT('%', #{searchword}, '%') OR product_name LIKE CONCAT('%', #{searchword}, '%') ) ORDER BY expiry_date LIMIT #{cri.startPage}, #{cri.pageSize}

